# Generated by re2c
# re2py $INPUT -o $OUTPUT

from enum import Enum
import os

BUFSIZE = 4096

class State:
    def __init__(self, fname):
        self.file = open(fname, "rb")
        self.buf = bytearray(BUFSIZE)
        self.lim = BUFSIZE - 1 # exclude terminating null
        self.cur = self.lim
        self.mar = self.lim
        self.tok = self.lim
        self.eof = False

    def __del__(self):
        self.file.close()

class Status(Enum):
    OK = 0
    EOF = 1
    LONG_LEXEME = 2

def fill(st):
    if st.eof:
        return Status.EOF

    # Error: lexeme too long. In real life could reallocate a larger buffer.
    if st.tok < 1:
        return Status.LONG_LEXEME

    # Shift buffer contents (discard everything up to the current token).
    st.buf = st.buf[st.tok:st.lim]
    st.cur -= st.tok;
    st.mar -= st.tok;
    st.lim -= st.tok;
    st.tok = 0;

    # Fill free space at the end of buffer with new data from file.
    bytes = st.file.read(BUFSIZE - st.lim - 1) # -1 for sentinel
    if not bytes:
        st.eof = True # end of file
    else:
        st.lim += len(bytes);
        st.buf += bytes

    st.buf += b'\0' # append sentinel

    return Status.OK

def lex(st, count):
    while True:
        st.tok = st.cur
        
        yystate = 0
        while True:
            match yystate:
                case 0:
                    yych = st.buf[st.cur]
                    match yych:
                        case 0x20:
                            st.cur += 1
                            yystate = 3
                            continue
                        case 0x27:
                            st.cur += 1
                            yystate = 5
                            continue
                        case _:
                            if st.cur >= st.lim:
                                if fill(st) == Status.OK:
                                    yystate = 0
                                    continue
                                yystate = 10
                                continue
                            st.cur += 1
                            yystate = 1
                            continue
                case 1:
                    yystate = 2
                    continue
                case 2:
                    return -1
                case 3:
                    yych = st.buf[st.cur]
                    match yych:
                        case 0x20:
                            st.cur += 1
                            yystate = 3
                            continue
                        case _:
                            if st.cur >= st.lim:
                                if fill(st) == Status.OK:
                                    yystate = 3
                                    continue
                            yystate = 4
                            continue
                case 4:
                    break
                case 5:
                    st.mar = st.cur
                    yych = st.buf[st.cur]
                    if yych >= 0x01:
                        yystate = 7
                        continue
                    if st.cur >= st.lim:
                        if fill(st) == Status.OK:
                            yystate = 5
                            continue
                        yystate = 2
                        continue
                    st.cur += 1
                    yystate = 6
                    continue
                case 6:
                    yych = st.buf[st.cur]
                    yystate = 7
                    continue
                case 7:
                    match yych:
                        case 0x27:
                            st.cur += 1
                            yystate = 8
                            continue
                        case 0x5C:
                            st.cur += 1
                            yystate = 9
                            continue
                        case _:
                            if st.cur >= st.lim:
                                if fill(st) == Status.OK:
                                    yystate = 6
                                    continue
                                yystate = 11
                                continue
                            st.cur += 1
                            yystate = 6
                            continue
                case 8:
                    count += 1
                    break
                case 9:
                    yych = st.buf[st.cur]
                    if yych <= 0x00:
                        if st.cur >= st.lim:
                            if fill(st) == Status.OK:
                                yystate = 9
                                continue
                            yystate = 11
                            continue
                        st.cur += 1
                        yystate = 6
                        continue
                    st.cur += 1
                    yystate = 6
                    continue
                case 10:
                    return count
                case 11:
                    st.cur = st.mar
                    yystate = 2
                    continue
                case _:
                    raise "internal lexer error"


def main():
    fname = "input"

    # Prepare input file.
    f = open(fname, "w")
    for i in range(BUFSIZE):
        f.write("'qu\0tes' 'are' 'fine: \\'' ")
    f.close()

    # Run lexer on the prepared file.
    st = State(fname)
    assert lex(st, 0) == 3 * BUFSIZE

    # Cleanup.
    os.remove(fname)

if __name__ == '__main__':
    main()
